              <div className="table-row" id="secondImage">
                <div className="table-cell w-2/12">
                </div>
                <div className="table-cell w-1/12">
                  <img
                  src={require("../images/gradcindy.png")}
                  alt="Picture of Cindy Qian" />
                </div>
                <div className="table-cell w-2/12">
                </div>
                <div className="table-cell">
