                <div className="table-cell w-1/12">
                </div>
                <div className="table-cell w-1/12">
                </div>
                <div className="table-cell w-1/12">
                </div>
              </div>


            </div>
          </Parallax>






        </main>

        <footer className="row-start-3 flex gap-6 flex-wrap items-center justify-center">
        </footer>
      </div>
  );
}

export default App;
